import { ColorSwatch } from '@/components/primitives';

const colors = ['#3B82F6', '#10B981', '#EF4444'];

<FlexRow gap="sm">
  {colors.map(color => (
    <ColorSwatch
      key={color}
      color={color}
      isActive={selectedColor === color}
      onPress={() => setSelectedColor(color)}
    />
  ))}
</FlexRow>

import { FileUpload } from '@/components/primitives';

<FileUpload
  accept={{ 'image/*': ['.png', '.jpg'] }}
  maxSize={5242880} // 5MB
  onFilesSelected={(files) => {
    console.log('Files:', files);
  }}
>
  <div className="border-2 border-dashed p-8">
    Drop images here or click to browse
  </div>
</FileUpload>

import { Input } from '@/components/primitives';

<Input
  value={email}
  onChange={setEmail}
  type="email"
  placeholder="Enter your email"
  error={!!emailError}
  errorMessage={emailError}
/>

const [enabled, setEnabled] = useState(false);

<Switch
  value={enabled}
  onChange={setEnabled}
  label="Enable dark mode"
  size="medium"
/>

import { RadioGroup } from '@/components/primitives';

const options = [
  { label: 'Light Theme', value: 'light' },
  { label: 'Dark Theme', value: 'dark' },
  { label: 'System', value: 'system' }
];

<RadioGroup
  options={options}
  value={theme}
  onChange={setTheme}
  direction="vertical"
/>

import { ScrollContainer } from '@/components/primitives';

<ScrollContainer
  height="lg"
  showBorder
  borderRadius="md"
>
  {messages.map(msg => (
    <MessageItem key={msg.id} {...msg} />
  ))}
</ScrollContainer>

import { Select } from '@/components/primitives';

const options = [
  { label: 'Small', value: 'sm' },
  { label: 'Medium', value: 'md' },
  { label: 'Large', value: 'lg' }
];

<Select
  options={options}
  value={selectedSize}
  onChange={setSelectedSize}
  placeholder="Choose size"
/>

import { TextArea } from '@/components/primitives';

<TextArea
  value={feedback}
  onChange={setFeedback}
  placeholder="Share your feedback..."
  rows={6}
  error={!!feedbackError}
  errorMessage={feedbackError}
/>
