codeExample.code:
import { Callout } from '@/components/primitives';

<Callout
  // Required
  variant="info" // 'info' | 'success' | 'warning' | 'error'
  children="Your message content here"

  // Optional sizing
  size="md" // 'xs' | 'sm' | 'md'

  // Layout variants
  layout="base" // 'base' | 'minimal'

  // Dismissible behavior
  dismissible={false}
  onClose={handleClose}

  // Auto-close timer
  autoClose={5} // seconds

  // Styling
  className="custom-class"

  // Testing
  testID="callout-test-id"
>
  Your callout message content
</Callout>

import { Button } from '@/components/primitives';

<Button
  // Core props
  onClick={handleClick}
  children="Button Text"

  // Button types (choose one)
  type="primary" // 'primary' | 'secondary' | 'light' | 'light-outline' | 'subtle' | 'subtle-outline' | 'danger' | 'primary-white' | 'secondary-white' | 'light-white' | 'light-outline-white' | 'disabled-onboarding' | 'unstyled'

  // Sizes
  size="normal" // 'compact' | 'small' | 'normal' | 'large'

  // States
  disabled={false}

  // Layout
  fullWidth={false}

  // Icons
  iconName="plus" // FontAwesome icon name
  iconOnly={false} // Show only icon without text
  icon={false} // Legacy prop for compatibility

  // Tooltips
  tooltip="Helpful tooltip text"
  highlightedTooltip={false}

  // Styling
  className="custom-class"

  // Accessibility
  id="button-id"
/>

import { ColorSwatch } from '@/components/primitives';

const colors = ['#3B82F6', '#10B981', '#EF4444'];

<FlexRow gap="sm">
  {colors.map(color => (
    <ColorSwatch
      key={color}
      color={color}
      isActive={selectedColor === color}
      onPress={() => setSelectedColor(color)}
    />
  ))}
</FlexRow>

import { FileUpload } from '@/components/primitives';

<FileUpload
  accept={{ 'image/*': ['.png', '.jpg'] }}
  maxSize={5242880} // 5MB
  onFilesSelected={(files) => {
    console.log('Files:', files);
  }}
>
  <div className="border-2 border-dashed p-8">
    Drop images here or click to browse
  </div>
</FileUpload>

import { Input } from '@/components/primitives';

<Input
  // Core props
  value={inputValue}
  onChange={setInputValue}
  placeholder="Enter text..."

  // Variants
  variant="filled" // 'filled' | 'bordered' | 'minimal' | 'onboarding'

  // Input types
  type="text" // 'text' | 'email' | 'password' | 'number' | 'tel' | 'url' | 'search'

  // States
  disabled={false}
  autoFocus={false}
  noFocusStyle={false}

  // Error handling
  error={hasError}
  errorMessage="This field is required"

  // Labels
  label="Field Label"
  labelType="static" // 'static' | 'floating'
  required={true}
  helperText="Additional help text"

  // Event handlers
  onFocus={() => console.log('focused')}
  onBlur={() => console.log('blurred')}
  onKeyDown={(e) => console.log('key:', e.key)}

  // Styling
  className="custom-class"
  style={{ width: '100%' }}

  // Accessibility
  testID="input-test-id"
  accessibilityLabel="Accessible label"
/>

import { Modal } from '@/components/primitives';

<Modal
  // Required props
  visible={isOpen}
  title="Modal Title"
  onClose={handleClose}
  children={<div>Modal content</div>}

  // Sizing
  size="medium" // 'small' | 'medium' | 'large'

  // Close behavior
  hideClose={false} // Hide the X button
  closeOnBackdropClick={true} // Close when clicking outside
  closeOnEscape={true} // Close with Escape key

  // Layout
  noPadding={false} // Remove default padding
  titleAlign="left" // 'left' | 'center'

  // Styling
  className="custom-modal-class"

  // Native-specific props
  // swipeToClose={true} // React Native only
  // swipeUpToOpen={false} // React Native only
  // keyboardAvoidingView={true} // React Native only
>
  <div>Your modal content here</div>
</Modal>

import { Switch } from '@/components/primitives';

const [enabled, setEnabled] = useState(false);

<Switch
  // Required props
  value={enabled}
  onChange={setEnabled}

  // States
  disabled={false}

  // Sizing (web)
  size="medium" // 'small' | 'medium' | 'large'
  variant="default" // Only 'default' currently supported

  // Styling
  className="custom-switch"
  style={{ margin: 10 }}

  // Accessibility
  accessibilityLabel="Toggle dark mode"
  testID="dark-mode-switch"

  // Native-specific props
  // hapticFeedback={true} // iOS haptic feedback
  // trackColorFalse="#767577" // Android track color when off
  // trackColorTrue="#81b0ff" // Android track color when on
  // thumbColor="#f5dd4b" // Thumb color for both platforms
/>

import { RadioGroup } from '@/components/primitives';

const options = [
  { label: 'Light Theme', value: 'light' },
  { label: 'Dark Theme', value: 'dark' },
  { label: 'System', value: 'system' }
];

<RadioGroup
  options={options}
  value={theme}
  onChange={setTheme}
  direction="vertical"
/>

import { ScrollContainer } from '@/components/primitives';

<ScrollContainer
  height="lg"
  showBorder
  borderRadius="md"
>
  {messages.map(msg => (
    <MessageItem key={msg.id} {...msg} />
  ))}
</ScrollContainer>

import { Select } from '@/components/primitives';

const options = [
  { label: 'Small', value: 'sm' },
  { label: 'Medium', value: 'md' },
  { label: 'Large', value: 'lg' }
];

<Select
  options={options}
  value={selectedSize}
  onChange={setSelectedSize}
  placeholder="Choose size"
/>

import { TextArea } from '@/components/primitives';

<TextArea
  value={feedback}
  onChange={setFeedback}
  placeholder="Share your feedback..."
  rows={6}
  error={!!feedbackError}
  errorMessage={feedbackError}
/>

import { Text } from '@/components/primitives';

<Text
  // Content
  children="Your text content"

  // Visual variants
  variant="default" // 'default' | 'strong' | 'subtle' | 'muted' | 'error' | 'danger' | 'success' | 'warning' | 'link'

  // Typography
  size="base" // 'xs' | 'sm' | 'base' | 'lg' | 'xl' | '2xl' | '3xl'
  weight="normal" // 'normal' | 'medium' | 'semibold' | 'bold'
  align="left" // 'left' | 'center' | 'right'

  // Spacing (mobile-optimized)
  marginBottom={16}
  marginTop={8}
  lineHeight={1.5}

  // Colors
  color="custom-color" // Custom color override

  // Web-specific props
  as="p" // 'span' | 'p' | 'h1' | 'h2' | 'h3' | 'h4' | 'h5' | 'h6' | 'div' | 'a'
  style={{ fontFamily: 'custom' }}
  onClick={handleClick}

  // Link props (when as="a" or variant="link")
  href="https://example.com"
  target="_blank"
  rel="noopener noreferrer"

  // Styling
  className="custom-class"

  // Testing & Accessibility
  testId="text-element"
  // Native: accessibilityLabel, accessible, etc.
>
  Your text content here
</Text>
